package processer

import (
	"encoding/base64"
	"fmt"
	"os"
	"strings"
	"testing"
)

func TestBlobToFile(t *testing.T) {

	content := "data:audio/webm;base64,"
	BlobToFile(content)

}

func BlobToFile(content string) {

	index := strings.Index(content, ",")
	decodeString, err := base64.StdEncoding.DecodeString(content[index+1:])
	if err != nil {
		fmt.Println("decode error:", err)
		return
	}

	fileName := "E:\\Coding\\go project\\ChatRobot\\docs\\audio\\1111.wav"
	err = os.WriteFile(fileName, decodeString, 0666)
	if err != nil {
		fmt.Println("语音文件存储错误", err)
	}
}
